import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [tables, setTables] = useState([]);
  const [selectedTable, setSelectedTable] = useState("");
  const [data, setData] = useState([]);

  // Get all table names
  useEffect(() => {
    fetch("http://localhost:5000/api/tables")
      .then((res) => res.json())
      .then((tables) => {
        setTables(tables);
        if (tables.length > 0) {
          setSelectedTable(tables[0]);
        }
      });
  }, []);

  // Get data for selected table
  useEffect(() => {
    if (selectedTable) {
      fetch(`http://localhost:5000/api/${selectedTable}`)
        .then((res) => res.json())
        .then((data) => setData(data));
    }
  }, [selectedTable]);

  // Generate Excel-style column letters (A, B, C...)
  const getColumnLetters = (count) =>
    Array.from({ length: count }, (_, i) =>
      String.fromCharCode(65 + i) // 65 = 'A'
    );

  return (
    <div className="App">
      <h1>Dynamic Table Output</h1>
      <div style={{ marginBottom: 24 }}>
        <select
          value={selectedTable}
          onChange={(e) => setSelectedTable(e.target.value)}
        >
          {tables.map((table) => (
            <option key={table} value={table}>
              {table}
            </option>
          ))}
        </select>
      </div>

      <div className="table-container">
        <table>
          <thead>
            {/* Excel-style blank row */}
            <tr>
              <th className="numbering"></th>
              {data.length > 0 &&
                getColumnLetters(Object.keys(data[0]).length).map((letter) => (
                  <th className="ab-header" key={letter}>
                    {letter}
                  </th>
                ))}
            </tr>

            {/* Row with 1, column headers */}
            <tr>
              <th className="numbering">1</th>
              {data.length > 0 &&
                Object.keys(data[0]).map((key) => <th key={key}>{key}</th>)}
            </tr>
          </thead>

          <tbody>
            {data.map((row, idx) => (
              <tr key={idx}>
                <td className="numbering">{idx + 2}</td>
                {Object.keys(row).map((key) => (
                  <td key={key}>{row[key]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
