import React, { useState } from "react";
import "./App.css";
import Table1 from "./table1";
import Table2 from "./table2";

function App() {
  const [visibleTables, setVisibleTables] = useState({ table1: true, table2: true });
  const [selectedTable, setSelectedTable] = useState("table1");

  const handleToggle = () => {
    setVisibleTables((prev) => ({
      ...prev,
      [selectedTable]: !prev[selectedTable],
    }));
  };

  return (
    <div className="App">
      <h1>Table Output & Processing</h1>
      <div style={{ marginBottom: 24 }}>
        <select
          value={selectedTable}
          onChange={(e) => setSelectedTable(e.target.value)}
          style={{ marginRight: 8 }}
        >
          <option value="table1">Table 1</option>
          <option value="table2">Table 2</option>
        </select>
        <button onClick={handleToggle}>
          {visibleTables[selectedTable] ? "Hide" : "Show"} {selectedTable === "table1" ? "Table 1" : "Table 2"}
        </button>
      </div>
      {visibleTables.table1 && <Table1 />}
      {visibleTables.table2 && <Table2 />}
    </div>
  );
}

export default App;
