import React, { useState, useEffect } from "react";
import "./App.css";
import Table1 from "./table1";
import Table2 from "./table2";

function App() {
  const [selectedTable, setSelectedTable] = useState("");
  const [tableList, setTableList] = useState([]);
  const [data, setData] = useState([]);

  // Fetch list of tables first
  const fetchTableList = () => {
    fetch("http://localhost:5000/api/tables")
      .then(res => res.json())
      .then(data => {
        setTableList(data);
        if (data.length > 0) {
          setSelectedTable(data[0]); // set first table as default
        }
      });
  };

  // Fetch data of selected table
  const fetchData = (table) => {
    fetch(`http://localhost:5000/api/${table}`)
      .then(res => res.json())
      .then(data => setData(data));
  };

  useEffect(() => {
    fetchTableList();
  }, []);

  useEffect(() => {
    if (selectedTable) {
      fetchData(selectedTable);
    }
  }, [selectedTable]);

  return (
    <div className="App">
      <h1>Table Output & Processing</h1>
      <div style={{ marginBottom: 24 }}>
        <select
          value={selectedTable}
          onChange={(e) => setSelectedTable(e.target.value)}
          style={{ marginRight: 8 }}
        >
          {tableList.map((table) => (
            <option key={table} value={table}>
              {table}
            </option>
          ))}
        </select>
        <button onClick={() => fetchData(selectedTable)}>Load Data</button>
      </div>
      <Table1 data={data} />
      <Table2 data={data} />
    </div>
  );
}

export default App;
