import React, { useState, useEffect } from "react";
import "./App.css";
import Table1 from "./table1";
import Table2 from "./table2";

function App() {
  const [tables, setTables] = useState([]);
  const [selectedTable, setSelectedTable] = useState("");
  const [data, setData] = useState([]);

  const fetchTables = async () => {
    const res = await fetch("http://localhost:5000/api/tables");
    const tables = await res.json();
    setTables(tables);
    if (tables.length > 0) setSelectedTable(tables[0]);
  };

  const fetchData = async () => {
    if (!selectedTable) return;
    const res = await fetch(`http://localhost:5000/api/${selectedTable}`);
    const data = await res.json();
    setData(data);
  };

  useEffect(() => {
    fetchTables();
  }, []);

  useEffect(() => {
    fetchData();
  }, [selectedTable]);

  return (
    <div className="App">
      <h1>Table Output & Processing</h1>
      <div style={{ marginBottom: 24 }}>
        <select
          value={selectedTable}
          onChange={(e) => setSelectedTable(e.target.value)}
          style={{ marginRight: 8 }}
        >
          {tables.map((table) => (
            <option key={table} value={table}>
              {table}
            </option>
          ))}
        </select>
        <button onClick={fetchData}>Load Data</button>
      </div>

      {selectedTable === "table1" && <Table1 data={data} />}
      {selectedTable === "table2" && <Table2 data={data} />}
    </div>
  );
}

export default App;
